import React, { useEffect, useState } from 'react'
import './Chat.css'
import ChatHeader from './ChatHeader';

import AddCircleIcon from '@material-ui/icons/AddCircle';
import CardGiftcardIcon from '@material-ui/icons/CardGiftcard';
import GifIcon from '@material-ui/icons/Gif';
import EmojiEmotionsIcon from '@material-ui/icons/EmojiEmotions';
import Message from './Message';
import { useSelector } from 'react-redux';
import { selectUser } from './features/userSlice';
import { selectChannelId, selectChannelName } from './features/appSlice';
import db from './firebase';

import firebase from 'firebase'; //no local

function Chat() {

    useEffect(() => {

        if(channelId){

            db.collection('channels')
            .doc(channelId)
            .collection('messages')
            .orderBy('timestamp', 'desc')
            .onSnapshot((snapshot) => 
            setMessages(snapshot.docs.map(doc => doc.data())));
        }
        
    }, [channelId]);

    const sendMessage = e => {
        e.preventDefault(); //Qd on envoie un mess on veut pas que ca refresh la page

        db.collection('channels').doc(channelId).collection('messages').add({
            timestamp:firebase.firestore.FieldValue.serverTimestamp(),
            message:input,
            user:user,
        })
    }

    const user = useSelector(selectUser);
    const channelId = useSelector(selectChannelId);
    const channelName = useSelector(selectChannelName);

    const [input,setInput] = useState('');

    const [messages, setMessages] = useState([]);


    return (
        <div className="chat">
            <ChatHeader channelName={channelName}></ChatHeader>

            <div className="chat__messages">
                {messages.map(message => (
                    <Message/>
                ))}
            </div>

            <div className="chat__input">
                <AddCircleIcon fontSize="large"/>
                <form>
                    <input value={input} disabled={!channelId} onChange={e => setInput(e.target.value)} placeholder={`Message #${channelName}`}/>
                    <button className="chat__inputButton" type="submit" onClick={sendMessage}>Send message</button>
                </form>

                <div className="chat__inputIcons">
                    <CardGiftcardIcon fontSize="large"/>
                    <GifIcon fontSize="large"/>
                    <EmojiEmotionsIcon fontSize="large"/>
                </div>

            </div>
        </div>
    )
}

export default Chat;
